mapper.insert:
INSERT INTO schedule
SET content=#{content}
, user_project_no = (
SELECT A.user_project_no FROM user_project A
WHERE user_no=#{scheduleUserNo}
AND project_no=#{projectNo})

, detail=#{detail}

mapper.select:
SELECT A.cheked
FROM schedule A
WHERE schedule_no=#{scheduleNo}

mapper.update:
UPDATE schedule
SET
user_project_no = (
SELECT A.user_project_no FROM user_project A
WHERE user_no=#{scheduleUserNo}
AND project_no=#{projectNo})

    , content = #{content}
    , detail = #{detail}

WHERE schedule_no=#{scheduleNo}
